/**
  * @brief  Get electrical ADC measurements.
  * @param[in] rawADC Pointer to the raw ADC values array.
  * @param[out] analog Pointer to the ADC struct to store the results.
  * @param[out] feedback Pointer to the Feedback struct to store id and iq.
  * @param[in] sinTheta_e Electrical angle sine (-1..1)
  * @param[in] cosTheta_e Electrical angle cosine (-1..1)
  */
void get_currents_voltage(volatile uint16_t rawADC[], volatile Analog* analog, volatile Feedback* feedback, volatile InverterError *errors, float sinTheta_e, float cosTheta_e) {

	// Calculate currents and voltage
//    float ia = get_linear(ADC_raw[0], CURRENT_SLOPE, CURRENT_OFFSET);
//    float ib = get_linear(ADC_raw[1], CURRENT_SLOPE, CURRENT_OFFSET);
//    float ic = get_linear(ADC_raw[2], CURRENT_SLOPE, CURRENT_OFFSET);

    float ia = get_linear(rawADC[0], CURRENT_SLOPE, analog->currentOffsets[0]);
    float ib = get_linear(rawADC[1], CURRENT_SLOPE, analog->currentOffsets[1]);
    float ic = get_linear(rawADC[2], CURRENT_SLOPE, analog->currentOffsets[2]);


    float vDC = get_linear(rawADC[3], VOLTAGE_SLOPE, VOLTAGE_OFFSET);

    // Store the measurements
    analog->ia = ia;
    analog->ib = ib;
    analog->ic = ic;
    analog->vDC = vDC;

    //TODO: Fix voltage acquisition (hardware issue)
    analog->vDC = 20.0F;

    // Define variables to store DQ currents
    float idMeas, iqMeas;

    // Pass pointers to idMeas and iqMeas
    get_idiq(ia, ib, ic,  sinTheta_e, cosTheta_e, &idMeas, &iqMeas);

    // Store DQ currents in feedback struct
    feedback->idMeas = idMeas;
    feedback->iqMeas = iqMeas;


    // Check for overcurrent/overvoltage/undervoltage faults
    if (fabs(analog->ia) > OVERCURRENT_TH || fabs(analog->ib) > OVERCURRENT_TH || fabs(analog->ic) > OVERCURRENT_TH) {
        set_error(errors, OVERCURRENT);
    } else {
        clear_error(errors, OVERCURRENT);
    }
    if (analog->vDC > OVERVOLTAGE_TH) {
		set_error(errors, OVERVOLTAGE);
	} else {
		clear_error(errors, OVERVOLTAGE);
	}
    if (analog->vDC < UNDERVOLTAGE_TH) {
		set_error(errors, UNDERVOLTAGE);
	} else {
		clear_error(errors, UNDERVOLTAGE);
	}
}

/**
  * @brief  Convert ADC reading to physical measurement with linear response.
  * @param[in]  bits The ADC reading.
  * @param[in]  slope The slope (units per volt).
  * @param[in]  offset The offset (volts at zero).
  * @retval measurement The physical measurement.
  */
float get_linear(uint32_t bits, float slope, float offset) {
    // Convert ADC reading to voltage
    float voltage = (float) bits * 0.0008058608f; // Assuming 12-bit ADC and 3.3V reference (bits * (3.3 V / 4095.0 bits))

    // Convert voltage to physical measurement
    float measurement = (voltage - offset) * slope;

    return measurement;
}

/**
 * @brief Computes d-q currents from current measurements and electrical angle.
 *
 * This function computes the d-q currents from phase currents (ABC), theta_e, and stores
 * the results in the provided pointers.
 *
 * @param[in] ia Phase A current in A.
 * @param[in] ib Phase B current in A.
 * @param[in] ic Phase C current in A.
 * @param[in] sinTheta_e Electrical angle sine (-1..1)
 * @param[in] cosTheta_e Electrical angle cosine (-1..1)
 * @param[out] idMeas Pointer to store the D-axis current.
 * @param[out] iqMeas Pointer to store the Q-axis current.
 */
void get_idiq(float ia, float ib, float ic, float sinTheta_e, float cosTheta_e, float *idMeas, float *iqMeas) {

	// Compute alpha beta using Clarke transformation
    float alpha = ia;
    float beta = (ia - 2.0F*(ia+ic)) * ISQ3;
    // float beta = (ia + 2.0F*ib) * ISQ3;

    // Park transformation
    *idMeas = alpha * cosTheta_e + beta * sinTheta_e;  // d = alpha * cos(theta_e) + beta * sin(theta_e)
    *iqMeas = beta * cosTheta_e - alpha * sinTheta_e;  // q = beta * cos(theta_e) - alpha * sin(theta_e)

}

/**
 * @brief Retrieves temperature from a lookup table based on ADC bits.
 *
 * This function retrieves temperature from a lookup table based on the ADC bits.
 * The lookup table (LUT) must have a value for each possible ADC bit combination.
 *
 * @param[in] bits ADC reading converted to bits.
 * @param[in] tempLUT Lookup table containing temperature values.
 * @return Temperature corresponding to the provided ADC bits.
 */
float get_temperature(uint32_t bits, const float tempLUT[]){
    return tempLUT[bits];
}

/**
 * @brief Calibrate the current sensor offsets.
 *
 * This function calculates the average offset for each current sensor channel
 * by reading the ADC values when no current is flowing. The calculated offsets
 * are used to correct the sensor readings.
 *
 * @param[in] rawADC Buffer containing the raw ADC values for the channels.
 * @param[out] currentOffsets Array to store the calculated offsets for each current channel.
 * @param[in] numSamples Number of samples to average for the offset calculation.
 */
void calibrate_offsets(volatile uint16_t rawADC[], volatile float currentOffsets[], uint32_t numSamples) {
    uint32_t sumADC[3] = {0}; // Summing ADC values for IA, IB, IC

    // Take multiple samples to calculate the average offset
    for (int i = 0; i < numSamples; ++i) {
        // ADC_raw is updated by the DMA, so no need to start the ADC conversion here

    	if (i > numSamples / 2.0F){
			// Accumulate the ADC readings
			sumADC[0] += rawADC[0];
			sumADC[1] += rawADC[1];
			sumADC[2] += rawADC[2];
    	}
    }

    // Calculate average offset
    currentOffsets[0] = 2 * (float)sumADC[0] / numSamples * 0.0008058608f; // Assuming 12-bit ADC
    currentOffsets[1] = 2 * (float)sumADC[1] / numSamples * 0.0008058608f;
    currentOffsets[2] = 2 * (float)sumADC[2] / numSamples * 0.0008058608f;
}

